CREATE TABLE IF NOT EXISTS users (
	user_id SERIAL PRIMARY KEY NOT NULL,
	username VARCHAR(255) NOT NULL UNIQUE,
	email VARCHAR(60) NOT NULL,
	password VARCHAR (5000),
	first_name VARCHAR(255),
	last_name VARCHAR(255),
	ship_to_address VARCHAR(75),
	ship_to_city VARCHAR(50),
	ship_to_state VARCHAR(2),
	ship_to_country VARCHAR(2),
	ship_to_zip INTEGER,
	bill_to_address VARCHAR(75),
	bill_to_city VARCHAR(50),
	bill_to_state VARCHAR(2),
	bill_to_country VARCHAR(2),
	bill_to_zip INTEGER
);

CREATE TABLE IF NOT EXISTS items (
	cut_id SERIAL PRIMARY KEY NOT NULL,
	api_cut_id INTEGER,
	price_per_pound NUMERIC,
	cut_name VARCHAR(50),
	cut_description TEXT,
	cut_image TEXT
);

CREATE TABLE IF NOT EXISTS cart (
	cart_id SERIAL PRIMARY KEY,
	user_id INT REFERENCES users(user_id),
	active BOOLEAN NOT NULL,
	date TIMESTAMP
);

CREATE TABLE IF NOT EXISTS cart_item (
	cart_item_id SERIAL PRIMARY KEY,
	cut_id INT REFERENCES items(cut_id),
	cart_id INT REFERENCES cart(cart_id),
	qty INT NOT NULL
);




